import './cars.css';
//import styles from 'https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css';
import $ from 'jquery';
import React from 'react';

import {useEffect, useState} from 'react';


const Cars = (props) => {
    const [stylePath] = useState(
        "https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"
      );
    const [allProductsList, setAllProductsList] = useState([]);
    $(document).ready(function(){
        $(".filter-dropdown, .text-button").click(function(){
          $(".edit-filter-modal").toggleClass("hidden");
          
        });
          $(".apply-button").click(function(){
          $(".edit-filter-modal").toggleClass("hidden");
                $(".filter, .filter-remove, .fa-plus, .fa-filter").toggleClass("filter-hidden");
            $(".filter-dropdown-text").text("Add filter");
          
            
          });
            
            $(".filter-remove").click(function(){
              $(".filter, .filter-remove, .fa-plus, .fa-filter").toggleClass("filter-hidden");
              $(".filter-dropdown-text").text("Filter dataset");
            });
      });
    useEffect(() => {
        fetch('http://127.0.0.1:5000/api/v1/car/catalog', {
            method: 'GET',
        })
        .then(res=>{
            if(res.ok){
                return res.json()
            }else{
                alert("something is wrong")
            }
        }).then(data =>{
            //console.log(data)
            setAllProductsList(data);
            
            }
        ).catch((err) => {
            console.error(err)
        }
        );
    },[])
    const CarRentsHandler = (car_image, car_name, car_price, car_year) => {
        localStorage.setItem('car_image', car_image);
        localStorage.setItem('car_image', car_image);
        localStorage.setItem('car_name', car_name);
        localStorage.setItem('car_price', car_price);
        localStorage.setItem('car_year', car_year);
        props.carRentsHandler();
    }
    //React Hooks: useEffect() is called twice even if an empty array is used as an argument
    //StrictMode renders components twice (on dev but not production)
    //in order to detect any problems with your code and warn you about them (which can be quite useful).
    //https://stackoverflow.com/questions/39974210/why-componentdidmount-gets-called-multiple-times-in-react-js-redux

    console.log(allProductsList)
    
   
    return(
        <div>

          <link rel="stylesheet" type="text/css" href={stylePath} />
          <div className="cars">

<div class="filter-bar">
  <a class="filter-dropdown">
    <i class="fa fa-filter" aria-hidden="true"></i>
    <span class="filter-dropdown-text">Filter dataset</span> 
  </a>
  <a class="filter filter-hidden">Region <span class="operator">=</span> North America, Asia </a>
  <a class="filter-remove filter-hidden">&times;</a>

</div>

<div class="edit-filter-modal hidden"><select>
  <option>Region</option>
  <option>String</option>
  <option>Date</option>
  <option>Number</option>
  </select>

  <input type="date" value="2018-07-22"
       min="2018-01-01" max="2024-12-31"/>
  <button class="apply-button">Apply</button><button class="text-button">Cancel</button>
</div>
          <div className="cars_row">
          {allProductsList.map(car =>
            <div className="cars_col">
                <div class="courses-image">
                    <img src={car.imgUrl} width="330" height="180" className="img-responsive" alt=""/>
                </div>
                <div className="cars_detail">
                    <h3><a>{car.name}</a></h3>
                    <p className="lead"> <strong>${car.price}</strong> <small>per day</small></p>
                </div>
                <div className="courses-info">
                <a onClick={ () => {
                    CarRentsHandler(car.imgUrl, car.name, car.price, car.year)
                }}>Rent Now</a>
                </div> 
            </div>   
            )}
          </div>
          <div className="navi">
            
              <ul className="navi__row">
                <li className="navi__item"><a>1</a></li>
                <li className="navi__item"><a>2</a></li>
                <li className="navi__item"><a>3</a></li>
                <li className="navi__item"><a>4</a></li>
                <li className="navi__item"><a>→</a></li>
              </ul>

          </div>
     </div>
        </div>
        
    )
}

export default Cars;
