import './cars.css';
import {useEffect, useState} from 'react';


const Cars  = (props) => {
    const [stylePath] = useState(
        "https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"
      );
    const [allProductsList, setAllProductsList] = useState([]);
    const getCar = async (url) => {
        console.log("fuck")
        fetch(url, {
            method: 'GET',
        })
        .then(res=>{
            if(res.ok){
                return res.json()
            }else{
                alert("something is wrong")
            }
        }).then(data =>{
            //console.log(data)
            setAllProductsList(data);
            
            }
        ).catch((err) => {
            console.error(err)
        }
        );
     }
    useEffect(() => {
        getCar('http://127.0.0.1:5000/api/v1/car/catalog/Default')
    },[])
    
    const CarRentsHandler = (car_image, car_name, car_price, car_year) => {
        localStorage.setItem('car_image', car_image);
        localStorage.setItem('car_image', car_image);
        localStorage.setItem('car_name', car_name);
        localStorage.setItem('car_price', car_price);
        localStorage.setItem('car_year', car_year);
        props.carRentsHandler();
    }
    //React Hooks: useEffect() is called twice even if an empty array is used as an argument
    //StrictMode renders components twice (on dev but not production)
    //in order to detect any problems with your code and warn you about them (which can be quite useful).
    //https://stackoverflow.com/questions/39974210/why-componentdidmount-gets-called-multiple-times-in-react-js-redux
    
    const [isActive, setActive] = useState("false");

    const handleToggle = () => {
      setActive(!isActive);
    };
    const [sort, setSort] = useState();
    const [start, setStartTime] = useState();
    const [stop, setStopTime] = useState();

    const onChangeSort = (event) => {
        setSort(event.target.value);
    }

    const onStartTime = (event) => {
        setStartTime(event.target.value);
    }
    
    const onStopTime = (event) => {
        setStopTime(event.target.value);
    }

    const apply_filter = () => { 
       // getCar('http://127.0.0.1:5000/api/v1/car/catalog/'+sort)
        fetch('http://127.0.0.1:5000/api/v1/car/catalog/'+sort, {
            method: 'GET',
        })
        .then(res=>{
            if(res.ok){
                return res.json()
            }else{
                alert("something is wrong")
            }
        }).then(data =>{
            //console.log(data)
            setAllProductsList(data);
            
            }
        ).catch((err) => {
            console.error(err)
        }
        );
    }
    return(
    <div>  
        <link rel="stylesheet" type="text/css" href={stylePath} />
        <div className="filter-section">
        <div className="filter-bar">
            <a className="filter-dropdown"  onClick={handleToggle}  >
            <i className="fa fa-filter" aria-hidden="true"></i>
            <span className="filter-dropdown-text">Filter dataset</span> </a>
        </div>

        <div classNameName={`edit-filter-modal-${isActive ? "opacity" : "unopacity"}`}>
            <select id = 'sort_by' onChange={onChangeSort}>
            <option>Default</option>
            <option>By price</option>
            <option>By year</option>
            </select>
            <input type="date" id = 'start' min="2020-01-01" max="2024-12-31" onChange={onStartTime}/>
            <input type="date" id = 'stop' min="2020-01-01" max="2024-12-31" onChange={onStopTime}/>
            <button className="apply-button" onClick={apply_filter}>Apply</button><button className="text-button" onClick={handleToggle}>Cancel</button>
        </div>
        </div>
        <div classNameName="cars">

          <div classNameName="cars_row">
          {allProductsList.map(car =>
            <div classNameName="cars_col">
                <div className="courses-image">
                    <img src={car.imgUrl} width="330" height="180" classNameName="img-responsive" alt=""/>
                </div>
                <div classNameName="cars_detail">
                    <h3><a>{car.name}</a></h3>
                    <p classNameName="lead"> <strong>${car.price}</strong> <small>per day</small></p>
                </div>
                <div classNameName="courses-info">
                <a onClick={ () => {
                    CarRentsHandler(car.imgUrl, car.name, car.price, car.year)
                }}>Rent Now</a>
                </div> 
            </div>   
            )}
          </div>
          <div classNameName="navi">
            
              <ul classNameName="navi__row">
                <li classNameName="navi__item"><a>1</a></li>
                <li classNameName="navi__item"><a>2</a></li>
                <li classNameName="navi__item"><a>3</a></li>
                <li classNameName="navi__item"><a>4</a></li>
                <li classNameName="navi__item"><a>â†’</a></li>
              </ul>

          </div>
        </div>
    </div>
        
    )
}

export default Cars;
