import './cars.css';
import carImage from '../../images/clemme.jpg';
import React from 'react';
//import axios from 'axios';
import {useEffect, useState} from 'react';

const Cars = (props) => {
    const onCarRentsHandler = () => {
        props.carRentsHandler();
    }
    const URL_PRODUCTS = 'http://127.0.0.1:5000/api/v1/car/catalog';


    const [allProductsList, setAllProductsList] = useState([]);

    
    useEffect(() => {
        fetchProducts();
    },[])
    //React Hooks: useEffect() is called twice even if an empty array is used as an argument
    //StrictMode renders components twice (on dev but not production)
    //in order to detect any problems with your code and warn you about them (which can be quite useful).
    //https://stackoverflow.com/questions/39974210/why-componentdidmount-gets-called-multiple-times-in-react-js-redux

    const fetchProducts = () => {
        fetch('http://127.0.0.1:5000/api/v1/car/catalog', {
            method: 'GET',
        })
        .then(res=>{
            if(res.ok){
                return res.json()
            }else{
                alert("something is wrong")
            }
        }).then(data =>{
            //console.log(data)
            setAllProductsList(data);
            
            }
        ).catch((err) => {
            console.error(err)
        }
        );
    }
    console.log(allProductsList)
    
    const turtles = [
        {
            name: "Leonardo",
            nickName: "Leo",
            weapon: "Katana",
            imgUrl: "https://upload.wikimedia.org/wikipedia/en/e/ed/Leonardo_%28Teenage_Mutant_Ninja_Turtles%29.jpg"
        },
        {
            name: "Donatello",
            nickName: "Don",
            weapon: "Bo staff",
            imgUrl: "https://upload.wikimedia.org/wikipedia/en/5/5a/Donatello_%28Teenage_Mutant_Ninja_Turtles%29.jpg"
        },
        {
            name: "Michelangelo",
            nickName: "Mikey",
            weapon: "Nunchucks",
            imgUrl: "https://upload.wikimedia.org/wikipedia/en/f/f3/Michelangelo_%28Teenage_Mutant_Ninja_Turtles%29.jpg"
        },
        {
            name: "Raphael",
            nickName: "Raph",
            weapon: "Sai",
            imgUrl: "https://upload.wikimedia.org/wikipedia/en/7/72/Raphael_%28Teenage_Mutant_Ninja_Tutles%29.jpg"
        }
    ]
