import './cars.css';
import carImage from '../../images/clemme.jpg';
import {useEffect} from 'react';

const Cars = (props) => {
    const onCarRentsHandler = () => {
        props.carRentsHandler();
    }
    useEffect(() => {
        const authToken = localStorage.getItem('token');
        url ="http://127.0.0.1:5000/api/v1/user/" +  localStorage.getItem('login');
        fetch(url, 
        {
        method: 'GET',
        headers: {
            'Authorization': 'Bearer ' + authToken
          }}
          ).then(res=>{
            if(res.ok){
                return res.json()
            }else{
                alert("something is wrong")
            }
        }).then(data =>{
            //token = localStorage.getItem('token');
            console.log(data)
            //document.querySelector("#firstName").placeholder = data["firstName"];
            }
        ).catch((err) => {
            console.error(err)
        }
        );

     });
    const turtles = [
        {
            name: "Leonardo",
            nickName: "Leo",
            weapon: "Katana",
            imgUrl: "https://upload.wikimedia.org/wikipedia/en/e/ed/Leonardo_%28Teenage_Mutant_Ninja_Turtles%29.jpg"
        },
        {
            name: "Donatello",
            nickName: "Don",
            weapon: "Bo staff",
            imgUrl: "https://upload.wikimedia.org/wikipedia/en/5/5a/Donatello_%28Teenage_Mutant_Ninja_Turtles%29.jpg"
        },
        {
            name: "Michelangelo",
            nickName: "Mikey",
            weapon: "Nunchucks",
            imgUrl: "https://upload.wikimedia.org/wikipedia/en/f/f3/Michelangelo_%28Teenage_Mutant_Ninja_Turtles%29.jpg"
        },
        {
            name: "Raphael",
            nickName: "Raph",
            weapon: "Sai",
            imgUrl: "https://upload.wikimedia.org/wikipedia/en/7/72/Raphael_%28Teenage_Mutant_Ninja_Tutles%29.jpg"
        }
    ]
    return(
        <div className="cars">
          <div className="cars_row">
          {turtles.map((turtle, index) =>
            <div key={turtle.name + index}>
                <h1>{turtle.name} ({turtle.nickName})</h1>
                <p>Weapon of choice: {turtle.weapon}</p>
                <img src={turtle.imgUrl} alt={`${turtle.name}`} width="200"/>
                <hr/>
            </div>
            )}
          </div>
          <div className="navi">
            
              <ul className="navi__row">
                <li className="navi__item"><a>1</a></li>
                <li className="navi__item"><a>2</a></li>
                <li className="navi__item"><a>3</a></li>
                <li className="navi__item"><a>4</a></li>
                <li className="navi__item"><a>â†’</a></li>
              </ul>

          </div>
     </div>
    )
}

export default Cars;
