import './cars.css';
import {useEffect, useState} from 'react';


const Cars  = (props) => {

    const [allProductsList, setAllProductsList] = useState([]);

    useEffect(() => {
        fetch('http://127.0.0.1:5000/api/v1/car/catalog', {
            method: 'GET',
        })
        .then(res=>{
            if(res.ok){
                return res.json()
            }else{
                alert("something is wrong")
            }
        }).then(data =>{
            //console.log(data)
            setAllProductsList(data);
            
            }
        ).catch((err) => {
            console.error(err)
        }
        );
    },[])
    const CarRentsHandler = (car_image, car_name, car_price, car_year) => {
        localStorage.setItem('car_image', car_image);
        localStorage.setItem('car_image', car_image);
        localStorage.setItem('car_name', car_name);
        localStorage.setItem('car_price', car_price);
        localStorage.setItem('car_year', car_year);
        props.carRentsHandler();
    }
    //React Hooks: useEffect() is called twice even if an empty array is used as an argument
    //StrictMode renders components twice (on dev but not production)
    //in order to detect any problems with your code and warn you about them (which can be quite useful).
    //https://stackoverflow.com/questions/39974210/why-componentdidmount-gets-called-multiple-times-in-react-js-redux

    console.log(allProductsList)
    
   
    return(
        <div>
          <div className="cars">
          <div class="edit-filter">
  <select id = 'select1'>
  <option>Defoult</option>
  <option>By price</option>
  <option>By year</option>
  </select>
  <input type="date" id = 'start' min="2020-01-01" max="2024-12-31"/>
  <input type="date" id = 'stop' min="2020-01-01" max="2024-12-31"/>
  <button class="apply-button">Apply</button>
         </div>
          <div className="cars_row">
          {allProductsList.map(car =>
            <div className="cars_col">
                <div class="courses-image">
                    <img src={car.imgUrl} width="330" height="180" className="img-responsive" alt=""/>
                </div>
                <div className="cars_detail">
                    <h3><a>{car.name}</a></h3>
                    <p className="lead"> <strong>${car.price}</strong> <small>per day</small></p>
                </div>
                <div className="courses-info">
                <a onClick={ () => {
                    CarRentsHandler(car.imgUrl, car.name, car.price, car.year)
                }}>Rent Now</a>
                </div> 
            </div>   
            )}
          </div>
          <div className="navi">
            
              <ul className="navi__row">
                <li className="navi__item"><a>1</a></li>
                <li className="navi__item"><a>2</a></li>
                <li className="navi__item"><a>3</a></li>
                <li className="navi__item"><a>4</a></li>
                <li className="navi__item"><a>→</a></li>
              </ul>

          </div>
     </div>
        </div>
        
    )
}

export default Cars;
