import './car_info.css';
import {useEffect, useState} from 'react';

const CarInfo = () => {
    const [CarInfo, setAllProductsList] = useState([]);
    const getCar = async (url) => {
        //console.log("fuck")
        fetch(url, {
            method: 'GET',
        })
        .then(res=>{
            if(res.ok){
                return res.json()
            }else{
                alert("something is wrong")
            }
        }).then(data =>{
            //console.log(data)
            setAllProductsList(data);
            
            }
        ).catch((err) => {
            console.error(err)
        }
        );
     }
    useEffect(() => {
        getCar('http://127.0.0.1:5000/api/v1/car/' + localStorage.getItem('car_id'))
    },[])

    const makeReservation = async (url) => {
        const data = {  "firstName":firstName, "lastName":lastName,"login":login, "email":email, "phone":phone, "password":password, "user_status":"User"};
        console.log(data)
        const res = await fetch("http://127.0.0.1:5000/api/v1/user", 
        {
        method: 'POST',
        headers: {
            'Content-type': 'application/json',
            'Accept': 'application/json'
        },
        body:JSON.stringify(data)});
        //let lol = '';
        if(res.ok){
            //lol = await res.json();
            alert("Reservation successful");
        }else{
            alert("something is wrong")
        }  
     }
    return(
        <div className = "car_list">
            <div className="car_list_row">
                <div className="car_list_col1">
                    <img src={CarInfo.imgUrl} width="650" height="330"/>
                </div>
                <div className="car_list_col2">
                    <h3>{CarInfo.name}</h3>
                    <p>This project was designed for one of the biggest Swiss entertainment centers that 
                    grew in popularity in 2009. 
                    Everything was completed in less than a month.
                    </p>
                    <div className ="divade_line"></div>
                    <div className ="car_info">
                        <ul>
                            <li>&#10003; 4-door sedan</li>
                        </ul>
                        <ul>
                            <li>&#10003; Auto transmission</li>
                        </ul>
                        <ul>
                            <li>&#10003; 4cyl 2.1L engine</li>
                        </ul>
                        <ul>
                            <li>&#10003; {CarInfo.productionYear}</li>
                        </ul>
                    </div>
                    <a className="rent">RENT NOW</a>
                </div>
            </div>
        </div>  
    )
}

export default CarInfo;
