import './App.css';
import Header from './component/header/header';
import CarsTitle from './component/title/cars/cars_title';
import HomeTitle from './component/title/home/home_title';
import PopularCars from './component/popular_cars/popular_cars';
import Footer from './component/footer/footer';
import Cars from './component/cars/cars';
import RentCar from './component/title/rent_car/rent_car';
import CarInfo from './component/car_info/car_info';
import Login from './component/login/login';
import {useEffect, useState} from 'react';


function App() {
  const [isHome, setIsHome] = useState(true);
  const [isCars, setIsCars] = useState(false);
  const [isRentCars, setIsRentCars] = useState(false);
  const [isLogin, setLogin] = useState(false);

  let token = localStorage.getItem('token');

  // console.log(typeof(token));


    if(token !== 'null' || token !== undefined || token !== 0){
      console.log(true);
      // setIsLoginAcount(true);
    }else{
      console.log(false);
      // setIsLoginAcount(false);
    }


  const LogOut = () => {
    // setIsLoginAcount(false);
    localStorage.setItem('token', null);
  }

  const homeHandler = () => {
    setIsHome(true);
    setIsCars(false);
    setIsRentCars(false);
    setLogin(false);
  }
  const carsHandler = () => {
    setIsCars(true);
    setIsHome(false);
    setIsRentCars(false);
    setLogin(false);
  }

  const carRentsHandler = () => {
    setIsRentCars(true);
    setIsHome(false);
    setIsCars(false);
    setLogin(false);
  }

  const loginHandler = () => {
    setLogin(true);
    setIsHome(false);
    setIsCars(false);
    setIsRentCars(false);
  }

  return (
    <div>
      <Header homeHandler={homeHandler} carsHandler={carsHandler} loginHandler={loginHandler} LogOut={LogOut}/>

      {isHome && <HomeTitle/>}
      {isHome && <PopularCars/>}

      {isCars && <CarsTitle/>}
      {isCars && <Cars carRentsHandler={carRentsHandler}/>}


      {isRentCars && <RentCar/>}
      {isRentCars && <CarInfo/>}

      {isLogin && <Login/>}
      <Footer/>
    </div>
  );
}

export default App;
